	<script type="text/javascript">
	function cerrarPopup(){
		var x = document.getElementById("popupCuisine");
		x.remove();
	}
	// Detecta la URL Actual
	var URLactual = window.location;
	// Variable que tiene la url del home
	var home = 'http://127.0.0.1/popup/';
	
	if(URLactual == home){	
		document.write("<div id='popupCuisine'>");
		document.write("<p><span onclick='cerrarPopup()'>Cerrar</span></p>");		
		document.write('<img src="banner-popup-LCA.jpg" alt="">');
		document.write('</div>');
		
	}/*else{
		window.location.href = 'http://www.google.com'; 
	}*/
	</script>
